mapper.select:
select
id, userid, name, time, address, img, type, isover, isplay
from koolrun.addact


        and id = #{id}


        and userid = #{userid}


        and name = #{name}


        and time = #{time}


        and address = #{address}


        and img = #{img}


        and type = #{type}


        and isover = #{isover}


        and isplay = #{isplay}


        and e = #{e}


        and w = #{w}

select
  id, userid, name, time, address, img, type, isover, isplay,e, w
from koolrun.addact
where userid = #{id}
limit #{offset}, #{limit}

select
id, userid, name, time, address, img, type, isover, isplay,e, w
from koolrun.addact
WHERE userid = #{id}
AND name LIKE #{string}
limit #{offset}, #{limit}
